import React from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

function AllPets() {
  const Navigate = useNavigate();
  const [allPets, setallPets] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:8000/api/pets")
      .then((result) => {
        setallPets(result.data);
      })
      .catch((err) => {
        console.log("Getting error in Client GetALLPETS", err);
      });
  }, []);

  const handleDelete = (id) => {
    axios
      .delete(`http://localhost:8000/api/pet/${id}`)
      .then((res) => {
        console.log(res.data);
        setallPets(allPets.filter((pet) => pet._id != id));
      })
      .catch((err) => {
        console.log("Getting error in Client Delete Pet", err);
      });
  };

  const display = allPets.map((pet) => {
    return (
      <div key={pet._id} className="border m-2 p-4">
        <h3>{pet.name[0].toUpperCase() + pet.name.toLowerCase().slice(1)}</h3>
        <p>
          {pet.description[0].toUpperCase() +
            pet.description.toLowerCase().slice(1)}
        </p>
        <p>{pet.type}</p>
        <p>{pet.skills.join(" ")}</p>
        <button onClick={() => handleDelete(pet._id)}>Delete</button> |
        <button onClick={() => Navigate(`/edit/${pet._id}`, { state: pet })}>
          Update
        </button>{" "}
        |<button onClick={() => Navigate(`/pet/${pet._id}`)}>Details</button>
      </div>
    );
  });

  return (
    <div>
      <Link to="/new">Add new Pet</Link>
      <h3>AllpetsAllpets</h3>
      {display}
    </div>
  );
}

export default AllPets;
