import React from "react";
import { useLocation, useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

function PetForm() {
  const { state } = useLocation();
  const { id } = useParams();
  const navigate = useNavigate();
  console.log(state);
  console.log(id);

  const [pet, setPet] = useState(
    state || { name: "", type: "", description: "", skills: [] }
  );

  useEffect(() => {
    if (id && !state) {
      console.log("coming here");
      axios
        .get(`http://localhost:8000/api/pet/${id}`)
        .then((res) => {
          console.log("Fetched single pet from axios", res.data);
          setPet(res.data);
        })
        .catch((err) => {
          console.log(
            "Error in Clinet side while Fetching the Single Pet",
            err
          );
        });
    }
  }, []);

  const handleInputChange = (e) => {
    if (e.target.name == "skills") {
      setPet({ ...pet, [e.target.name]: e.target.value.split(",") });
    } else {
      setPet({ ...pet, [e.target.name]: e.target.value });
    }
  };

  const createNewPet = () => {
    axios
      .post("http://localhost:8000/api/pets", pet)
      .then((res) => {
        console.log("Create new Pet", res.data);
        navigate("/");
      })
      .catch((err) => {
        console.log("Error in Clinet side while createin the NEW pet", err);
      });
  };

  const updateTheForm = () => {
    axios
      .put(`http://localhost:8000/api/pet/${id}`, pet)
      .then((res) => {
        console.log("Updated the existing Pet", res.data);
        navigate("/");
      })
      .catch((err) => {
        console.log(
          "Error in Client side while updating the existing pet",
          err
        );
      });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (id) {
      updateTheForm();
    } else {
      createNewPet();
    }
  };

  return (
    <div>
      <h1>{id ? "Edit the Pet Form" : "Create new Form"}</h1>
      <form onSubmit={handleSubmit}>
        <label>Name</label>
        <input
          type="text"
          name="name"
          value={pet.name}
          onChange={handleInputChange}
        />
        <label>Type</label>
        <input
          type="text"
          name="type"
          value={pet.type}
          onChange={handleInputChange}
        />
        <label>Description</label>
        <input
          type="text"
          name="description"
          value={pet.description}
          onChange={handleInputChange}
        />
        <label>Skills</label>
        <input
          type="text"
          name="skills"
          value={pet.skills}
          onChange={handleInputChange}
        />
        {/* <label>Skill options</label>
        <input
          type="text"
          name="skill"
          value={pet.skill}
          onChange={handleInputChange}
        /> */}

        <input type="submit" value={id ? "Update" : "Create"}></input>
      </form>
    </div>
  );
}

export default PetForm;
